mapper.select:
select Nsrztmc
from dwa_base_djxx
where nsrsbh = #{nsrsbh}

select sum(ynse)
from dwa_base_sbxx
where nsrsbh = #{nsrsbh}
and zsxmdm = #{zsxmdm}
and sssqq >= #{begindate}
and sssqz <#{enddate}
and rptflg in ('M', 'Q')

select sum(ynse)
 from dwa_base_sbxx
 where nsrsbh = #{nsrsbh}
 and zsxmdm = #{zsxmdm}
 and substr(sssqz, 1, 4) = #{year}
 and rptflg  in ('M', 'Q')

select sum(ysxse)
from dwa_base_sbxx
where nsrsbh = #{nsrsbh}
  and zsxmdm = #{zsxmdm}
  and substr(sssqz, 1, 4) = #{year}
  and rptflg  in ('M', 'Q')

select sum(ysxse)
from dwa_base_sbxx
where nsrsbh = #{nsrsbh}
and zsxmdm = #{zsxmdm}
and sssqq>= #{begindate}
and sssqz<#{enddate}
and rptflg in ('M', 'Q')

select ysxse as "ysxse",
       rptflg as "rptflg"
from dwa_base_sbxx
where nsrsbh = #{nsrsbh}
and zsxmdm = #{zsxmdm}
and sssqq>= #{begindate}
and sssqz <#{enddate}
and rptflg in ('M', 'Q')

select sum(ysxse)
 from dwa_base_sbxx
 where nsrsbh = #{nsrsbh}
 and zsxmdm = #{zsxmdm}
   and sssqq >= #{begindate}
   and sssqz <#{enddate}
 and rptflg in ('M', 'Q')

select count(1)
  from dwa_base_wfwzxx
 where nsrsbh = #{nsrsbh}
   and Sswfsd_dm in ('050202', '050204', '050204')
   and DJRQ >=  #{begindate}

select count(1)
   from dwa_base_wfwzxx
   where nsrsbh = #{nsrsbh}
   and djrq>=#{begindate}

select ysxse as "ysx",
DATE_FORMAT(sssqq, '%Y-%m-%d') AS "sssqz",
rptflg as "freq"
from dwa_base_sbxx
where nsrsbh = #{nsrsbh}
and zsxmdm = #{zsxmdm}
and sssqq >= #{begindate}
and sssqz <#{enddate}
and rptflg in ('M', 'Q')

select sum(ynse)
from dwa_base_sbxx
where nsrsbh = #{nsrsbh}
  and zsxmdm = '10104'
  and substr(sssqz, 1, 4) = #{year}
  and rptflg ='Q'
